package com.codewith.kotlinretrofit.apiimport com.codewith.kotlinretrofit.model.Dataimport com.codewith.kotlinretrofit.model.Datasimport com.codewith.kotlinretrofit.model.GetResponseimport com.codewith.kotlinretrofit.model.PostResponseimport org.json.JSONObjectimport retrofit2.Callimport retrofit2.http.Bodyimport retrofit2.http.GETimport retrofit2.http.POSTimport retrofit2.http.Urlinterface ApiServices {    @POST    fun empDetails(@Url url: String, @Body employee: JSONObject): Call<PostResponse>    @GET    fun getEmployee(@Url url: String): Call<List<GetResponse>>    @POST    fun empDetail(@Url url: String, @Body employee: JSONObject): Call<PostResponse>    @POST    fun appStatus(@Url url: String, @Body status: JSONObject): Call<Data>}